import React from 'react';

const Hero = () => {
    return (
        <div className="bg-dark-primary text-white py-20 px-8"> {/* Changed background to dark theme */}
            <div className="max-w-7xl mx-auto flex flex-col items-center justify-center"> {/* Centered content */}
                <div className="text-center max-w-3xl"> {/* Added max-width for better readability */}
                    <h1 
                        className="text-5xl font-bold leading-tight text-text-primary" 
                        data-aos="fade-up"
                        data-aos-delay="100"
                    >
                        Turning Invisible Work into Institutional Wisdom
                    </h1>
                    <p 
                        className="mt-6 text-lg text-text-secondary" 
                        data-aos="fade-up"
                        data-aos-delay="200"
                    >
                        AI-powered tools to capture knowledge, accelerate onboarding, and future-proof your organization.
                    </p>
                    <div 
                        className="mt-10 space-x-4"
                        data-aos="fade-up"
                        data-aos-delay="300"
                    >
                        {/* buttons */}
                    </div>
                </div>
                {/* Removed the illustration */}
            </div>
        </div>
    );
};

export default Hero;
